import gulp from 'gulp';
import path from 'path';
import nodemon from 'gulp-nodemon';

import buildCSS from './gulp/buildCSS';
import { frontendBuildProd, frontendWatch } from './gulp/buildJS';

const watchCss = (done) => {
  gulp.watch('./app/**/*.styl', buildCSS(false));
  done();
};

gulp.task('dev',
  gulp.series(
    buildCSS(false),
    gulp.parallel([
      watchCss,
      frontendWatch,
      (done) => nodemon({
        execMap: {
          js: 'node'
        },
        script: path.join(__dirname, 'server.js'),
        watch: ['./dist'],
        done,
      }).on('restart', () => { console.log('Restarted!'); })
    ]),
  )
);

gulp.task('build', gulp.series([frontendBuildProd, buildCSS(true)]));

