import fs from 'fs';
import path from 'path';
import nib from 'nib';
import gulp from 'gulp';
import stylus from 'gulp-stylus';

import walkDirectorySync from './helpers/walkDirectorySync';

const indexFileComparator = (p) => path.basename(p) === 'index.styl';
const stylusFileComparator = (p) => path.extname(p) === '.styl';

/**
 * The directories we want to search
 * Each directory can have a different comparator
 *
 * @type {Object}
 */
const pathComparators = {
  './app/styles': indexFileComparator,
  './app/pages': stylusFileComparator,
  './app/components': stylusFileComparator,
  './app/modules': stylusFileComparator,
};

export default (isProduction) => () => {
  // we use the hash created for the js file as cache-buster for the stylesheet
  const hash = isProduction ?
    fs.readFileSync(path.join(process.cwd(), 'hash.txt'))
    : 'dev';
  const destFile = `styles.${hash}.styl`
  const destFolder = './';
  const destPath = path.join(destFolder, destFile);

  const styleSheetStr = ['nib'].concat(
      Object.keys(pathComparators)
        .reduce((carry, path) => walkDirectorySync(path, carry, pathComparators[path]), [])
    )
    .reduce((carry, path) => `${carry} @require '${path}'\n`, '')

  fs.writeFileSync(destPath, styleSheetStr);
  console.log('compiling:', destPath);

  return gulp.src(destPath)
    .pipe(stylus({
      'include css': true,
      'hoist atrules': true,
      compress: true,
      use: [nib()]
    }))
    .pipe(gulp.dest('./dist/css'))
    .on('end', () => {
      try { fs.unlinkSync(destPath); } catch (e) {}
    });
};

