import fs from 'fs';
import path from 'path';

/**
 * Recursively walks the directory tree starting at `dir`,
 * collecting all filepaths which match `comparator`
 *
 * @param  {string}   dir        The directory to walk
 * @param  {array}    filelist   The file list
 * @param  {function} comparator The comparator
 *
 * @return {array}
 */
const walkDirectorySync = (dir, filelist = [], comparator = () => true) => {
  const files = fs.readdirSync(dir);

  files.forEach((file) => {
    const p = path.join(dir, file);
    if (fs.statSync(p).isDirectory()) {
      filelist = walkDirectorySync(p, filelist, comparator);
    } else {
      comparator(p) && filelist.push(p);
    }
  });

  return filelist;
};


export default walkDirectorySync;

