import gulp from 'gulp';
import webpack from 'webpack';
import fs from 'fs';
import path from 'path';
import ProgressBar from 'progress';

import productionConfig from '../webpack.config.production';
import developmentConfig from '../webpack.config.development';

const statsConfig = {
  colors: true,
  source: false,
  reasons: false,
  chunkModules: false,
  assets: false,
};

const onBuild = (done) => {
  return (err, stats) => {
    if (err) {
      console.log('Error', err);
    } else {
      console.log(stats.toString(statsConfig));
      fs.writeFileSync(path.resolve(__dirname, '../hash.txt'), stats.hash);
    }

    if (done) {
      done();
    }
  };
};

const useProgressBar = (wp) => {
  const bar = new ProgressBar(PROGRESS_FORMAT, {
    total: 100,
    width: 20,
    stream: process.stdout
  });
  wp.apply(new webpack.ProgressPlugin((percentage, msg) => {
    bar.update(percentage);
  }));
};

const PROGRESS_FORMAT = 'compiling: [:bar] :percent elapsed: :elapseds';

const frontendBuild = (config) => (done) => {
  const wp = webpack(config);
  useProgressBar(wp);
  return wp.run(onBuild(done));
};

export const frontendBuildProd = frontendBuild(productionConfig);

export const frontendWatch = (done) => {
  const nodemon = require('nodemon');

  const wp = webpack(developmentConfig);
  useProgressBar(wp);
  let doneCalled = false;

  // In order to have a fully built file prior to starting
  // nodemon, we listen for the done event.
  // We have to make sure to only call the callback once,
  // else gulp will complain about it.
  wp.plugin('done', (stats) => {
    if (!doneCalled) {
      onBuild(done)(null, stats);
      doneCalled = true;
    }
  });

  wp.watch(100, (err, stats) => {
    onBuild()(err, stats);
    // There are nodemon issues abound about this but they continue to go unaddressed
    // https://github.com/remy/nodemon/issues/1491
    // https://github.com/remy/nodemon/issues/1316
    // So for now, setTimeout seems to do the trick to allow the previously used port to free up
    setTimeout(() => {
      nodemon.restart();
    }, 3000);
  });
};

